import streamlit as st 
st.title("Chai Taste poll" )

col1, col2 = st.columns(2)
with col1:
    st.header("Masala Chai")
    st.image("data:image/jpeg;base64,")
    vote1 = st.button("vote Masala Chai")

with col2:
    st.header("Adrak Chai")
    st.image("data:image/jpeg;base64,")
    vote2 = st.button("vote Adrak Chai")
    
if vote1:
    st.success("Thanks for voting Masala Chai ☕️")
elif vote2: 
    st.success("Thanks for voting Adrak Chai ☕️")   
    
name = st.sidebar.text_input("Enter your name")
tea = st.sidebar.selectbox("Select your favorite tea", ("Masala Chai", "Adrak Chai", "Green Tea", "Black Tea"))

st.write(f"Hello {name}, you selected {tea} as your favorite tea!")

with st.expander("See Tea Facts"):
    st.write("""
    - Masala Chai is a spiced tea from India.
    - Adrak Chai is ginger tea, known for its health benefits.
    - Green Tea is rich in antioxidants.
    - Black Tea has a strong flavor and higher caffeine content.
    """)
    
st.markdown('### Thank you for participating in the Tea Poll! ☕️')
